function setupAiSearch() {
    const aiSearchBtn = document.getElementById("aiSearchBtn");
    const aiSearchInput = document.getElementById("aiSearchInput");
    const searchResultsContainer = document.getElementById("searchResultsContainer"); 

    aiSearchBtn.addEventListener("click", async () => {
        const query = aiSearchInput.value.trim();
        if (query) {
            searchResultsContainer.innerHTML = "กำลังค้นหา...";

            // API Key ของคุณ - ใส่ไว้ตรงนี้
            const YOUR_API_KEY = "AIzaSyDsyXfT8nrlG56wo3zL9x9W_KPv5uHsxMY"; 
            
            try {
                // สร้าง Prompt สำหรับ AI
                const prompt = `จากข้อมูลอ้างอิงด้านล่างนี้ ให้คุณค้นหาและสรุปคำตอบสำหรับคำถาม: "${query}" ในรูปแบบที่เป็นธรรมชาติและเข้าใจง่าย ถ้าข้อมูลไม่เพียงพอให้บอกว่าไม่พบข้อมูล\n\nข้อมูลอ้างอิง:\n${JSON.stringify(aiData)}`;

                const response = await fetch("https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=" + YOUR_API_KEY, {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify({
                        contents: [{
                            parts: [{ text: prompt }],
                        }],
                    }),
                });

                if (!response.ok) {
                    throw new Error("การค้นหาล้มเหลว");
                }
                const data = await response.json();
                
                const textResult = data.candidates[0].content.parts[0].text;
                
                searchResultsContainer.innerHTML = `<h3>ผลการค้นหาด้วย AI</h3><p>${textResult}</p>`;

            } catch (error) {
                console.error("เกิดข้อผิดพลาด:", error);
                searchResultsContainer.innerHTML = "ไม่สามารถทำการค้นหาด้วย AI ได้ในขณะนี้";
            }
        }
    });

    aiSearchInput.addEventListener("keyup", e => {
        if (e.key === "Enter") {
            aiSearchBtn.click();
        }
    });
}

